soup = BeautifulSoup(country_codes_table, 'html.parser')
table_body = soup.tbody
rows = table_body.find_all('tr')

countries = []

rows.pop(0) # remove the table header value
for row in rows:
    data = row.find_all('td')

    country_name = data[0].get_text()
    iso2 = data[1].get_text()
    iso3 = data[2].get_text()
    phone_code = data[3].get_text()
    continent = data[4].get_text()
    capital = data[5].get_text()
    time_zone = data[6].get_text()
    currency = data[7].get_text()

    country = {
        "country_name": country_name,
        "iso2": iso2,
        "iso3": iso3,
        "phone_code": phone_code,
        "continent": continent,
        "capital": capital,
        "time_zone": time_zone,
        "currency": currency
    }

    countries.append(country)


con = sqlite3.connect('data.db')
cursor = con.cursor()

for country in countries:
    insert_country_query = """
    INSERT INTO Countries VALUES ('%s', '%s', '%s', '%s', '%s', '%s', '%s', '%s')
    """ % (country["country_name"], country["iso2"], country["iso3"], country["phone_code"], country["continent"], country["capital"], country["time_zone"], country["currency"])

    cursor.execute(insert_country_query)

con.commit()
con.close()